USE `dbChurchill`;
CREATE  OR REPLACE VIEW lstFacturaCompra AS
SELECT
	fc.idFacturaCompra,
	fc.noFactura,
	fc.proveedor,
	fc.fechaFactura,
	fc.comentario,
	ef.idEstadoFactura,
	ef.estadoFactura,
	fce.usuario,
