    e.evento,
    e.fechaEvento,
    s.idSalon,
    s.salon,
    e.horaInicio,
    e.horaFinal,
    e.observacion,
    e.usuario,
    e.fechaRegistro,
    e.numeroPersonas,
    ee.idEstadoEvento,
    ee.estadoEvento,
    c.idCliente,
    nit,
    nombre,
    cui,
    correo,
    telefono,
    direccion,
    idTipoCliente,
    tipoCliente,
    e.idFactura
FROM evento AS e
	JOIN vstCliente AS c
		ON e.idCliente = c.idCliente
        
	JOIN estadoEvento AS ee
		ON e.idEstadoEvento = ee.idEstadoEvento
	
    JOIN salon AS s
		ON s.idSalon = e.idSalon
        
;

